import { Component } from '@angular/core';
import { AdminService } from '../service/admin.service';
import { CreateVet } from '../model/create-vet.model';

@Component({
  selector: 'app-register-vet',
  templateUrl: './register-vet.component.html',
  styleUrls: ['./register-vet.component.css']
})
export class RegisterVetComponent {
  public vet : CreateVet = new CreateVet();

  constructor(private adminService: AdminService) { }

  ngOnInit(): void {
    console.log(localStorage.getItem('token'));

  }

  public getDate(): string {
    let date = new Date();
    let day = date.getDate();
    let month = date.getMonth() + 1;
    let year = date.getFullYear();
    return `${year}-${month < 9 ? ('0'+month) : month}-${day < 9 ? ('0'+day) : day}`;
  }

  register(){
    if(this.isInputValid()){

      this.adminService.registerVet(this.vet).subscribe((response: any) => {
        console.log(response)

        alert('Successfully registered');
        // window.location.href = '/register-vet'

      },
      (error) => {
        alert("Invalid registration");
        console.log(error);
      });

    }
  }


  public isInputValid(): boolean {

    var regexp = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;     
    // Converting the email to lowercase
    if(!regexp.test(String(this.vet.email).toLowerCase())) {
      alert('email format is not valid')
      return false;
    }

    if (this.vet.firstName.trim() == '' || this.vet.lastName.trim() == '' || this.vet.email.trim() == ''
    || this.vet.password.trim() == '' || this.vet.dateOfBirth == null || this.vet.phoneNumber.trim() == ''
     || this.vet.personalId.trim() == '') {
        alert('Please fill in all fields!');
        return false;
     }

     return true;
  }
}
<div class="registration-container">
  <div class="registration-form">
    <h2>New vet</h2>
    <form (ngSubmit)="register()" class="form-two-columns">
      <div class="form-column">
      <div class="form-group">
        <label for="email">Email</label>
        <input type="email" id="email" name="email" placeholder="Enter your email" [(ngModel)]="vet.email" required>
      </div>

      <div class="form-group">
        <label for="password">Password</label>
        <input type="password" id="password" name="password" placeholder="Enter your password"  [(ngModel)]="vet.password" required>
      </div>

      <div class="form-group">
        <label for="firstName">First name</label>
        <input type="text" id="firstName" name="firstName" placeholder="Enter your first name"  [(ngModel)]="vet.firstName" required>
      </div>

      <div class="form-group">
        <label for="lastName">Last name</label>
        <input type="text" id="lastName" name="lastName" placeholder="Enter your last name"  [(ngModel)]="vet.lastName" required>
      </div>
    </div>

    <div class="form-column">
      <div class="form-group">
        <label for="dateOfBirth" class="form-label">Date of birth</label>
        <input type="date" id="dateOfBirth" name="dateOfBirth" [(ngModel)]="vet.dateOfBirth" max="{{getDate()}}">
    </div>

    <div class="form-group">
      <label for="phoneNumber" class="form-label">Phone number</label>
      <input type="text" id="phoneNumber" name="phoneNumber" [(ngModel)]="vet.phoneNumber">
  </div>

  <div class="form-group">
    <label for="personalId" class="form-label">Personal id</label>
    <input type="text" id="personalId" name="personalId" [(ngModel)]="vet.personalId">
</div>
    <button type="submit" class="btn-register">Register</button>
  </div>
</form>
  </div>
</div>
