import { Component } from '@angular/core';
import { AnimalService } from '../service/animal.service';
import { AdminService } from '../service/admin.service';
import { PhotoService } from '../service/photo.service';
import { AnimalWithPhotosDTO } from '../model/animal-with-photos-DTO.model';
import { AnimalTypeService } from '../service/animalType.service';

@Component({
  selector: 'app-register-animal',
  templateUrl: './register-animal.component.html',
  styleUrls: ['./register-animal.component.css']
})
export class RegisterAnimalComponent {
  public animal : AnimalWithPhotosDTO = new AnimalWithPhotosDTO();
  public selectedFile!: File;
  public selectedFiles: Array<File> = new Array()
  public fileURLs : Array<string> = new Array()
  public allAnimalTypes : Array<String> = new Array()
  public addAnimalTypeButton: Boolean = false
  public newType: string = ""

  constructor(private adminService: AdminService, private photoService: PhotoService, private animalService: AnimalService,
    private animalTypeService: AnimalTypeService) { }

  ngOnInit(): void {
    console.log(localStorage.getItem('token'));
    this.readAllAnimalTypes()
  }

  public getDate(): string {
    let date = new Date();
    let day = date.getDate();
    let month = date.getMonth() + 1;
    let year = date.getFullYear();
    return `${year}-${month < 9 ? ('0'+month) : month}-${day < 9 ? ('0'+day) : day}`;
  }

  register(){

    if(this.isInputValid()){

    this.selectedFiles.forEach(file => {
      const formData = new FormData();

      formData.append('image', file, file.name)

      console.log(file) 

      this.photoService.uploadMedia(formData).subscribe(response=>{
        console.log(response)
        // window.location.reload()

      })
        this.fileURLs.push(file.name)

        console.log(this.fileURLs)


    });
    //!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!REGISTRATION
      if(this.animal.sterilized == null){
        this.animal.sterilized = false
      }
      this.animal.photos = this.fileURLs

      console.log(this.fileURLs)
      console.log(this.animal)

        this.adminService.registerAnimal(this.animal).subscribe((response: any) => {
          console.log(response)

          alert('Successfully registered');

          window.location.href = '/unadopted-animals'
        },
        (error) => {
          alert("Invalid registration");
          console.log(error);
        });
      }

  }


  public isInputValid(): boolean {

    var regexp = /^\d+$/     
    // Converting the email to lowercase
    if(!regexp.test(String(this.animal.chipNumber))) {
      alert('Chip number must contain only numbers')
      return false;
    }

    if (this.animal.name.trim() == '' || this.animal.animalType.trim() == '' || this.animal.chipNumber.trim() == ''
    || this.animal.description.trim() == ''|| this.animal.gender.trim() == ''|| this.animal.location.trim() == '' 
    || this.animal.dateOfBirth == null || this.animal.size.trim() == '') {
        alert('Please fill in all fields!');
        return false;
     }

     return true;
  }

  uploadPhoto(event: any) {
    const selectedFiles: FileList = event.target.files;

    console.log(selectedFiles)

    if (selectedFiles.length > 0) {
      for (let i = 0; i < selectedFiles.length; i++) {
        this.selectedFile = <File>event.target.files[i]
        console.log(this.selectedFile)

        this.selectedFiles.push(this.selectedFile)

      }
    }

    console.log(this.selectedFiles)
  }

  readAllAnimalTypes() {

    this.animalService.allAnimalTypes().subscribe((response: any) => {
      console.log(JSON.parse(response))

      // @ts-ignore
      JSON.parse(response).forEach(typeObject => {

        this.allAnimalTypes.push(typeObject.animalType)
      });
    });
  }

  openAddNewTypeWindow(){
    this.addAnimalTypeButton = true;
  }

  addNewType(){
    console.log(this.newType)
    if(this.newType === ""){
      alert("You didn't write any new type")
    }
    else{
      this.animalTypeService.create(this.newType).subscribe((response: any) => {
        console.log(JSON.parse(response))
        alert("Successfully added new type")

        window.location.href = '/register-animal'

      });

    }
  }
}
<div class="registration-container">
  <div class="registration-form">
    <h2>New animal</h2>
    <form (ngSubmit)="register()" class="form-two-columns">
      <div class="form-column">
        <!-- First Column -->
        <div class="form-group">
          <label for="name">Name</label>
          <input type="text" id="name" name="name" placeholder="Enter animal's name" [(ngModel)]="animal.name" required>
        </div>

        <div class="form-group">
          <label for="chipNumber">Chip number</label>
          <input type="text" id="chipNumber" name="chipNumber" placeholder="Enter animal's Chip number" [(ngModel)]="animal.chipNumber" required>
        </div>

        <div class="form-group">
          <label for="location">Location</label>
          <input type="text" id="location" name="location" placeholder="Enter animal's location" [(ngModel)]="animal.location" required>
        </div>

        <div class="form-group">
          <label for="description">Description</label>
          <textarea id="description" name="description" placeholder="Enter animal's description" [(ngModel)]="animal.description" required></textarea>
        </div>

        <div class="form-group">
          <label for="dateOfBirth" class="form-label">Date of birth</label>
          <input type="date" id="dateOfBirth" name="dateOfBirth" [(ngModel)]="animal.dateOfBirth" max="{{getDate()}}">
        </div>
      </div>

      <div class="form-column">
        <!-- Second Column -->
        <div class="form-group">
          <label>Size</label>
          <select [(ngModel)]="animal.size" class="form-control form-control-lg" name="size">
            <option value="none" selected disabled hidden>Select an Option</option>
            <option value="Big">Big</option>
            <option value="Medium">Medium</option>
            <option value="Small">Small</option>
          </select>
        </div>

        <div class="form-group">
          <label>Type</label>
          <select [(ngModel)]="animal.animalType" class="form-control form-control-lg" name="size">
            <option *ngFor="let type of allAnimalTypes" [value]="type">{{type}}</option>
          </select>
        </div>

        <div class="form-group">
          <label>Gender</label>
          <select [(ngModel)]="animal.gender" class="form-control form-control-lg" name="size">
            <option value="none" selected disabled hidden>Select an Option</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
          </select>
        </div>

        <div class="form-group">
          <label>
            Is sterelized:
            <input type="checkbox" [(ngModel)]="animal.sterilized" id="sterilized" name="sterilized" />
          </label>
        </div>

        <div class="form-group">
          <input type="file" id="my-file" (change)="uploadPhoto($event)" multiple><br>
        </div>

        <button type="submit" class="btn-register">Register</button>
      </div>
    </form>
    <button class="btn-add-animal-type" (click)="openAddNewTypeWindow()">Add new animal type</button>
      <div *ngIf="addAnimalTypeButton" class="form-group">
        <input type="text" id="new-type" [(ngModel)]="newType"><br>
        <button class="btn-add-animal-type" (click)="addNewType()">Add</button>
      </div>
  </div>

</div>
