import { Component } from '@angular/core';
import { AnimalService } from '../service/animal.service';
import { SubscriptionService } from '../service/subscription.service';
import { Animal } from '../model/animal.model';

@Component({
  selector: 'app-animal-profile',
  templateUrl: './animal-profile.component.html',
  styleUrls: ['./animal-profile.component.css']
})
export class AnimalProfileComponent {

  selectedAnimalProfileId = localStorage.getItem('selectedAnimalProfileId')
  public selectedAnimalProfile : Animal = new Animal

  constructor(private animalService: AnimalService, private subscriptionService : SubscriptionService) { }

  ngOnInit(): void {
    console.log(localStorage.getItem('token'))
    console.log(this.selectedAnimalProfileId)

    this.read()
  }

  read(){
    this.animalService.read(this.selectedAnimalProfileId!).subscribe((response: any) => {
      console.log(JSON.parse(response))
      this.selectedAnimalProfile = JSON.parse(response)
    }
   );
  }
}
<body>
  <div class="profile-container">
    <div class="profile-header">
      <h1>Animal Profile</h1>
    </div>
    <div class="profile-content">
      <div class="profile-details">
        <h2 id="name">{{selectedAnimalProfile.name}}</h2>
        <p>Date of Birth: {{selectedAnimalProfile.dateOfBirth}}</p>
        <p>Location: {{selectedAnimalProfile.location}}</p>
        <p>Size: {{selectedAnimalProfile.size}}</p>
        <p>Is sterilized?: {{selectedAnimalProfile.sterilized}}</p>
        <p>Description: {{selectedAnimalProfile.description}}</p>
      </div>
      <div class="profile-pictures">
        <img class="profile-picture" src="animal-pic1.jpg" alt="Animal Picture 1">
        <img class="profile-picture" src="animal-pic1.jpg" alt="Animal Picture 1">
        <img class="profile-picture" src="animal-pic1.jpg" alt="Animal Picture 1">
        <img class="profile-picture" src="animal-pic1.jpg" alt="Animal Picture 1">
        <img class="profile-picture" src="animal-pic1.jpg" alt="Animal Picture 1">
        <img class="profile-picture" src="animal-pic1.jpg" alt="Animal Picture 1">
        <!-- Repeat the <img> tags for your images -->
      </div>
    </div>
  </div>
</body>
