import { Component } from '@angular/core';
import { LoginUser } from '../model/login-user.model';
import { UserService } from '../service/user.service';

@Component({
  selector: 'app-login-user',
  templateUrl: './login-user.component.html',
  styleUrls: ['./login-user.component.css']
})
export class LoginUserComponent {
  public loginUser : LoginUser = new LoginUser();
  public token : string = "";
  public role : string = "";
  public loggedUserId : string = "";

  constructor(private userService: UserService) { }

  ngOnInit(): void {
    console.log(localStorage.getItem('token'));

  }

  login(){
    this.userService.login(this.loginUser).subscribe((response: any) => {
      this.token = response;
      localStorage.setItem('token',this.token);
      console.log(this.token)

      let decodedJWT = JSON.parse(window.atob(this.token.split('.')[1]));

      alert('Successfully logged in');

      this.loggedUserId = decodedJWT.sub;
      localStorage.setItem('loggedUserEmail', this.loggedUserId);
      console.log(decodedJWT.sub);

      window.location.href = '/unadopted-animals'
    },
    (error) => {
      alert("Invalid email/password");
      console.log(error);
    }
   );
  } 
}

<body>
    <div class="login-container">
        <div class="form-container">
            <h2>Login</h2>
            <form action="#">
                <label for="username">Username:</label>
                <input type="text" id="username" name="username" required>

                <label for="password">Password:</label>
                <input type="password" id="password" name="password" required>

                <button type="submit">Login</button>
            </form>
        </div>
        <div class="photo-container">
            <!-- You can replace the image URL with your desired photo -->
            <img src="https://via.placeholder.com/400" alt="Login Photo">
        </div>
    </div>
</body>
